INSERT INTO `wp_postmeta` VALUES (468,128,'_faqタイトル8','field_63fa0e91635f8');
INSERT INTO `wp_postmeta` VALUES (469,128,'faq内容8','油は米油を使用しております。\r\n米油は、ビタミンEやリノール酸・オレイン酸などの不飽和脂肪酸が豊富で美容健康効果が期待できます。また加熱しても油っぽさが出にくくクッキーの風味を引き立たせてくれるため、フルオーツの油として選定しております。');
INSERT INTO `wp_postmeta` VALUES (470,128,'_faq内容8','field_63fa0e99635f9');
